// Import các thành phần cần thiết
import React from 'react';
import { View, Text, Image, FlatList, StyleSheet, TouchableOpacity } from 'react-native';

// Dữ liệu giả định cho danh sách sản phẩm
const products = [
  { id: 1, name: 'Product 1', price: '$19.99', image: require('./images/product1.jpg') },
  { id: 2, name: 'Product 2', price: '$29.99', image: require('./images/product2.jpg') },
  // Thêm dữ liệu sản phẩm khác...
];

// Component hiển thị mỗi sản phẩm trong danh sách
const ProductItem = ({ item }) => (
  <TouchableOpacity style={styles.productItem}>
    <Image source={item.image} style={styles.productImage} />
    <Text style={styles.productName}>{item.name}</Text>
    <Text style={styles.productPrice}>{item.price}</Text>
  </TouchableOpacity>
);

// Component chính hiển thị danh sách sản phẩm
const ProductListScreen = () => {
  return (
    <View style={styles.container}>
      <FlatList
        data={products}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => <ProductItem item={item} />}
        numColumns={2}
      />
    </View>
  );
};

// CSS cho các thành phần
const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  productItem: {
    flex: 1,
    margin: 8,
    padding: 16,
    backgroundColor: '#fff',
    borderRadius: 8,
    alignItems: 'center',
  },
  productImage: {
    width: 120,
    height: 120,
    borderRadius: 4,
    marginBottom: 8,
  },
  productName: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 4,
  },
  productPrice: {
    fontSize: 14,
    color: '#888',
  },
});

export default ProductListScreen;
