// HomeScreen.js
import React from 'react';
import { View, Text, ScrollView, StyleSheet } from 'react-native';
import { Card, Button, Image } from 'react-native-elements';
import Icon from 'react-native-vector-icons/MaterialIcons';

const products = [
  { id: 1, title: 'Giày thể thao', price: 10, image: require('../../assets/1.webp') },
  { id: 2, title: 'Giày thể thao', price: 15, image: require('../../assets/2.webp') },
  { id: 3, title: 'Giày thể thao', price: 8, image: require('../../assets/3.webp') },
  { id: 4, title: 'Giày thể thao', price: 7, image: require('../../assets/4.webp') },
  { id: 5, title: 'Giày thể thao', price: 7, image: require('../../assets/4.webp') },
  { id: 6, title: 'Giày thể thao', price: 7, image: require('../../assets/4.webp') },
  { id: 7, title: 'Giày thể thao', price: 7, image: require('../../assets/4.webp') },
  { id: 8, title: 'Giày thể thao', price: 8, image: require('../../assets/3.webp') },
];

const HomeScreen = () => {
  return (
    <ScrollView style={styles.container}>
      <View style={styles.rowContainer}>
        {products.map((product) => (
          <Card key={product.id} style={styles.card}>
            <Image
              source={product.image}
              style={styles.image}
            />
            <Text style={styles.price}>Price: ${product.price}</Text>
            <Button
              icon={<Icon name="add-shopping-cart" color="#ffffff" />}
              buttonStyle={styles.buyButton}
              title="BUY"
            />
          </Card>
        ))}
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    marginTop: 20,
  },
  rowContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  card: {
    width: '48%',
    marginBottom: 10,
  },
  image: {
    width: '100%',
    height: 150,
  },
  price: {
    marginBottom: 10,
  },
  buyButton: {
    borderRadius: 0,
    marginLeft: 0,
    marginRight: 0,
    marginBottom: 0,
  },
});

export default HomeScreen;
