import React from 'react';
import {
StyleSheet,
View,
Text,
Linking,
StyleProp,
TextStyle,
ViewStyle,
} from 'react-native';
import { Header as HeaderRNE, HeaderProps, Icon } from '@rneui/themed';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { SafeAreaProvider } from 'react-native-safe-area-context';

const products = [
  { id: 1, title: 'Giày thể thao', price: 10, image: require('../../assets/1.webp') },
  { id: 2, title: 'Giày thể thao', price: 15, image: require('../../assets/2.webp') },
  { id: 3, title: 'Giày thể thao', price: 8, image: require('../../assets/3.webp') },
  { id: 4, title: 'Giày thể thao', price: 7, image: require('../../assets/4.webp') },
  { id: 5, title: 'Giày thể thao', price: 7, image: require('../../assets/4.webp') },
  { id: 6, title: 'Giày thể thao', price: 7, image: require('../../assets/4.webp') },
  { id: 7, title: 'Giày thể thao', price: 7, image: require('../../assets/4.webp') },
  { id: 8, title: 'Giày thể thao', price: 8, image: require('../../assets/3.webp') },
];
const Cart = () => {

return (
  <SafeAreaProvider>
    <HeaderRNE
      leftComponent={{
        icon: 'menu',
        color: '#fff',
      }}      
      centerComponent={{ text: 'Header', style: styles.heading }}
    />
  </SafeAreaProvider>
);
};

const styles = StyleSheet.create({
headerContainer: {
  justifyContent: 'center',
  alignItems: 'center',
  backgroundColor: '#397af8',
  marginBottom: 20,
  width: '100%',
  paddingVertical: 15,
},
heading: {
  color: 'white',
  fontSize: 22,
  fontWeight: 'bold',
},
headerRight: {
  display: 'flex',
  flexDirection: 'row',
  marginTop: 5,
},
subheaderText: {
  color: 'white',
  fontSize: 16,
  fontWeight: 'bold',
},
});

export default Cart;
