import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
// import HomeScreen from '../screens/HomeScreen';
// import ProfileScreen from '../screens/ProfileScreen';
// import Cart from '../screens/Cart';
// import MenuScreen from '../screens/MenuScreen';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import { createStackNavigator } from '@react-navigation/stack';
// import Detail from '../screens/Detail';
// import Favorite from '../screens/Favorite';
import HomeScreen from './../screens/HomeScreen';
import Detail from './../screens/Detail';
import Cart from './../screens/Cart';
import MenuScreen from './../screens/MenuScreen';
import Favorite from './../screens/Favorite';

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

const HomeStack = () => {
  return (
    <Stack.Navigator initialRouteName="Home" screenOptions={{ headerShown: false }}>
      <Stack.Screen name="HomeStack" component={HomeScreen} />
      <Stack.Screen name="Details" component={Detail} />
      <Stack.Screen name="Cart" component={Cart} />
    </Stack.Navigator>
  );
}

const Menu = () => {
  return (
    <Tab.Navigator
      screenOptions={{
        headerShown: false,
        tabBarActiveTintColor: '#FFA500',
        tabBarStyle: { height: 60 },
        tabBarLabelStyle: { fontSize: 15 },
      }}
    >
      <Tab.Screen
        name="HomeTab"
        component={HomeStack}  // Sử dụng HomeStack thay vì HomeScreen
        options={{
          tabBarIcon: ({ color }) => (
            <Icon name="home" size={35} color={color} />
          ),
          tabBarLabel: 'Home',
        }}
      />
      <Tab.Screen
        name="AlertTab"
        component={MenuScreen}
        options={{
          tabBarIcon: ({ color }) => (
            <Icon name="bell-outline" size={35} color={color} />
          ),
          tabBarLabel: 'Alert',
        }}
      />
      <Tab.Screen
        name="FavoriteTab"
        component={Favorite}
        options={{
          tabBarIcon: ({ color }) => (
            <Icon name="heart-outline" size={35} color={color} />
          ),
          tabBarLabel: 'Favorite',
        }}
      />
      <Tab.Screen
        name="ProfileTab"
        component={ProfileScreen}
        options={{
          tabBarIcon: ({ color }) => (
            <Icon name="account-outline" size={35} color={color} />
          ),
          tabBarLabel: 'Profile',
        }}
      />
    </Tab.Navigator>
  );
}

export default Menu;
