import React from 'react';
import { Header as HeaderRNE, Icon } from '@rneui/themed';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';

const Header = ({ title }) => {
  return (
    <HeaderRNE
      leftComponent={{
        icon: () => <MaterialIcons name="menu" size={25} color="#fff" />,
        color: '#fff',
      }}      
      centerComponent={{ text: title, style: { color: 'white', fontSize: 22, fontWeight: 'bold' } }}
    />
  );
};

export default Header;
