// HomeScreen.js
import React from 'react';
import { View, Text } from 'react-native';
import { Card, Button } from 'react-native-elements';
import Icon from 'react-native-vector-icons/MaterialIcons';

const products = [
  { id: 1, title: "Giày thể thao", price: 10, image: require('../assets/1.webp') },
  { id: 2, title: "Giày thể thao", price: 15, priceSale: 10, image: require('../assets/2.webp') },
  { id: 3, title: "Giày thể thao", price: 8, priceSale: 10, image: require('../assets/3.webp') },
  { id: 4, title: "Giày thể thao", price: 7, image: require('../assets/4.webp') },
  { id: 5, title: "Giày thể thao", price: 12, image: require('../assets/5.webp') },

];

const HomeScreen = () => {
  return (
    <View>
      <Card
        title='HELLO WORLD'
        image={require('../../assets/ad.webp')}>
        <Text style={{ marginBottom: 10 }}>
          The idea with React Native Elements is more about component structure than actual design.
        </Text>
        <Button
          icon={<Icon name='code' color='#ffffff' />}
          buttonStyle={{ borderRadius: 0, marginLeft: 0, marginRight: 0, marginBottom: 0 }}
          title='VIEW NOW' />
      </Card>
    </View>
  );
};

export default HomeScreen;
