// HomeScreen.js
import React from 'react';
import { View, Text, ScrollView } from 'react-native'; // Import ScrollView for scrolling through product cards
import { Card, Button } from 'react-native-elements';
import Icon from 'react-native-vector-icons/MaterialIcons';

const products = [
  { id: 1, title: "Giày thể thao", price: 10, image: require('../../assets/1.webp') },
  { id: 2, title: "Giày thể thao", price: 15, priceSale: 10, image: require('../../assets/2.webp') },
  { id: 3, title: "Giày thể thao", price: 8, priceSale: 10, image: require('../../assets/3.webp') },
  { id: 4, title: "Giày thể thao", price: 7, image: require('../../assets/4.webp') },
];

const HomeScreen = () => {
  return (
    <View>
      {products.map((product) => (
        <Card key={product.id}>
           <Image
            source={ product.image }
            containerStyle={styles.item}
            PlaceholderContent={<ActivityIndicator />}
          />
          <Text style={{ marginBottom: 10 }}>Price: ${product.price}</Text>
          {product.priceSale && (
            <Text style={{ marginBottom: 10, color: 'red' }}>Sale Price: ${product.priceSale}</Text>
          )}
          <Button
            icon={<Icon name='code' color='#ffffff' />}
            buttonStyle={{ borderRadius: 0, marginLeft: 0, marginRight: 0, marginBottom: 0 }}
            title='VIEW NOW'
          />
        </Card>
      ))}
    </View>
  );
};

export default HomeScreen;
